INSERT INTO `config_info` VALUES (9, 'common.yaml', 'DEFAULT_GROUP', 'server:\n  tomcat:\n    max-swallow-size: -1\n  error:\n    include-exception: true\n    include-stacktrace: ALWAYS\n    include-message: ALWAYS\n  compression:\n    enabled: true\n    min-response-size: 1024\n    mime-types: application/javascript,application/json,application/xml,text/html,text/xml,text/plain,text/css,image/*\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"*\" #暴露所有节点\n    health:\n      sensitive: true #关闭过滤敏感信息\n  endpoint:\n    health:\n      show-details: ALWAYS  #显示详细信息\nspring:\n  servlet:\n    multipart:\n      max-file-size: 100MB\n      max-request-size: 100MB\n  #json 时间戳统一转换\n  jackson:\n    date-format:   yyyy-MM-dd HH:mm:ss\n    time-zone:   GMT+8\n  aop:\n    proxy-target-class: true\n  mvc:\n    static-path-pattern: /**\n  resource:\n    static-locations: classpath:/static/,classpath:/public/\n  datasource:\n    druid:\n      stat-view-servlet:\n        enabled: true\n        loginUsername: admin\n        loginPassword: 123456\n        allow:\n      web-stat-filter:\n        enabled: true\n      # 连接池的配置信息\n      # 初始化大小，最小，最大\n      initial-size: 5\n      min-idle: 5\n      maxActive: 20\n      # 配置获取连接等待超时的时间\n      maxWait: 60000\n      # 配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\n      timeBetweenEvictionRunsMillis: 60000\n      # 配置一个连接在池中最小生存的时间，单位是毫秒\n      minEvictableIdleTimeMillis: 300000\n      validationQuery: SELECT 1 FROM DUAL\n      testWhileIdle: true\n      testOnBorrow: false\n      testOnReturn: false\n      # 打开PSCache，并且指定每个连接上PSCache的大小\n      poolPreparedStatements: true\n      maxPoolPreparedStatementPerConnectionSize: 20\n      # 配置监控统计拦截的filters，去掉后监控界面sql无法统计，\'wall\'用于防火墙\n      filters: stat,wall,slf4j,config\n      # 通过connectProperties属性来打开mergeSql功能；慢SQL记录\n      # connectionProperties: druid.stat.mergeSql\\=true;druid.stat.slowSqlMillis\\=5000;config.decrypt=true;\n      connectionProperties: druid.stat.mergeSql\\=true;druid.stat.slowSqlMillis\\=5000;\n      url: jdbc:mysql://127.0.0.1:3306/test_ds?characterEncoding=UTF-8&useUnicode=true&useSSL=false&tinyInt1isBit=false&allowPublicKeyRetrieval=true&serverTimezone=Asia/Shanghai&allowMultiQueries=true&rewriteBatchedStatements=true\n      username: root\n      password: root\n      driver-class-name: com.mysql.cj.jdbc.Driver\n\n  #redis 配置\n  redis:\n    password: Cqt@hb4399\n    cluster:\n      nodes: 172.16.252.117:9001,172.16.252.118:9001,172.16.252.119:9001,172.16.252.123:9001,172.16.252.124:9001,172.16.252.125:9001\n    # 连接超时时间（毫秒）\n    timeout: 2000\n    jedis:\n      pool:\n        maxActive: 50\n        maxIdle: 30\n        minIdle: 5\n        max-wait: 3000\n\n#mybatis plus 设置\nmybatis-plus:\n  mapper-locations: classpath*:/mapper/**/*.xml\n  global-config:\n    # 关闭MP3.0自带的banner\n    banner: false\n    db-config:\n      #主键类型  0:\"数据库ID自增\",1:\"该类型为未设置主键类型\", 2:\"用户输入ID\",3:\"全局唯一ID (数字类型唯一ID)\", 4:\"全局唯一ID UUID\",5:\"字符串全局唯一ID (idWorker 的字符串表示)\";\n      id-type: AUTO\n      # 默认数据库表下划线命名\n      table-underline: true\n  configuration:\n    call-setters-on-nulls: true\n\n\ncommon :\n  # 本地：local\\Minio：minio\\阿里云：alioss\n  uploadType: local\n  path :\n    #文件上传根目录 设置\n    upload: /file/upFiles\n    #webapp文件路径\n    webapp: /file/webapp\n    #下载文件路径\n    download: /file/downloadFiles/industrial\n    # 映射路径\n    shineUpon: https://hb-dev.linkhyperion.cn:8007/industrial\n  \n  desform:\n    # 主题颜色（仅支持 16进制颜色代码）\n    theme-color: \"#1890ff\"\n    # 文件、图片上传方式，可选项：qiniu（七牛云）、system（跟随系统配置）\n    upload-type: system\n#Mybatis输出sql日志\nlogging:\n  level:\n    org.jeecg.modules.system.mapper : debug\nweb:\n  jwt:\n    expireTime: 1800\nphone:\n  jwt:\n    expireTime: 1800\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n', '9cd28ea1caff787466cb7b745f685dc4', '2023-04-05 00:56:40', '2023-04-05 00:57:03', 'nacos', '10.18.0.2', '', '', '', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (11, 'gateway.yaml', 'DEFAULT_GROUP', 'spring:\n  cloud:\n    gateway:\n      httpclient:\n        responseTimeout: 60000\n      discovery:\n        locator:\n          enabled: true\n      globalcors:\n        cors-configurations:\n          \'[/**]\':\n            allowCredentials: true\n            allowedOrigins: \"*\"\n            allowedMethods: \"*\"\n            allowedHeaders: \"*\"\n      # 全局熔断降级配置\n      default-filters:\n        - name: SpecialHystrix\n          args:\n            name: default\n            #转发地址\n            fallbackUri: \'forward:/fallback\'\n            hystrix: \n              - /water-api/api/getData:longTime\n        - name: Retry\n          args:\n            #重试次数，默认值是 3 次\n            retries: 3\n            #HTTP 的状态返回码\n            statuses: BAD_GATEWAY,BAD_REQUEST\n            #指定哪些方法的请求需要进行重试逻辑，默认值是 GET 方法\n            #            methods: GET,POST\n            methods: GET\nribbon:\n  restclient:\n    enabled: true\n  ConnectTimeout: 6000\n  ReadTimeout: 600000\n  OkToRetryOnAllOperations: false\n  MaxAutoRetriesNextServer: 1\n  MaxAutoRetries: 1\n  eager-load:\n    enabled: true\n\nhystrix:\n  enabled: true\n  shareSecurityContext: true\n  command:\n    default:\n      execution:\n        isolation:\n          strategy: SEMAPHORE\n          thread:\n            timeoutInMilliseconds: 600000\n          semaphore:\n             #信号量最大请求数配置，允许并发量1000的请求，默认情况下是10，也就是超过10个的并发会直接进入fallback方                          #法，不会去真正请求\n            maxConcurrentRequests: 2000\n      fallback:\n        isolation:\n          strategy: SEMAPHORE\n          semaphore:\n            maxConcurrentRequests: 1000\n    longTime:\n      execution:\n        isolation:\n          strategy: SEMAPHORE\n          thread:\n            timeoutInMilliseconds: 60000\n          semaphore:\n             #信号量最大请求数配置，允许并发量1000的请求，默认情况下是10，也就是超过10个的并发会直接进入fallback方                          #法，不会去真正请求\n            maxConcurrentRequests: 2000\n      fallback:\n        isolation:\n          strategy: SEMAPHORE\n          semaphore:\n            maxConcurrentRequests: 1000  \n\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"*\" #暴露所有节点\n    health:\n      sensitive: true #关闭过滤敏感信息\n\n\n\nexcludeUrls: /sys/api/*,sys/test/**\n#url传参方式校验的token\nrequestParamVerifyTokenUrl: /water-manager/download\nlogLevel: info\nlogging.config: classpath:logback-level.xml', '3a3308c8a325342c5349101c4a588409', '2023-04-05 00:57:24', '2023-04-05 01:54:01', 'nacos', '10.18.0.2', '', '', '', '', '', 'yaml', '');
