INSERT INTO `his_config_info` VALUES (1, 11, 'jeecg-dev.yaml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    druid:\n      stat-view-servlet:\n        enabled: true\n        loginUsername: admin\n        loginPassword: 123456\n        allow:\n      web-stat-filter:\n        enabled: true\n    dynamic:\n      druid: # 全局druid参数，绝大部分值和默认保持一致。(现已支持的参数如下,不清楚含义不要乱设置)\n        # 连接池的配置信息\n        # 初始化大小，最小，最大\n        initial-size: 5\n        min-idle: 5\n        maxActive: 20\n        # 配置获取连接等待超时的时间\n        maxWait: 60000\n        # 配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\n        timeBetweenEvictionRunsMillis: 60000\n        # 配置一个连接在池中最小生存的时间，单位是毫秒\n        minEvictableIdleTimeMillis: 300000\n        validationQuery: SELECT 1 FROM DUAL\n        testWhileIdle: true\n        testOnBorrow: false\n        testOnReturn: false\n        # 打开PSCache，并且指定每个连接上PSCache的大小\n        poolPreparedStatements: true\n        maxPoolPreparedStatementPerConnectionSize: 20\n        # 配置监控统计拦截的filters，去掉后监控界面sql无法统计，\'wall\'用于防火墙\n        filters: stat,wall,slf4j\n        # 通过connectProperties属性来打开mergeSql功能；慢SQL记录\n        connectionProperties: druid.stat.mergeSql\\=true;druid.stat.slowSqlMillis\\=5000\n\n      datasource:\n        master:\n          url: jdbc:mysql://jeecg-boot-mysql:3306/jeecg-boot?characterEncoding=UTF-8&useUnicode=true&useSSL=false&tinyInt1isBit=false&allowPublicKeyRetrieval=true&serverTimezone=Asia/Shanghai\n          username: root\n          password: root\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          # 多数据源配置\n          #multi-datasource1:\n          #url: jdbc:mysql://localhost:3306/jeecg-boot2?useUnicode=true&characterEncoding=utf8&autoReconnect=true&zeroDateTimeBehavior=convertToNull&transformedBitIsBoolean=true&allowPublicKeyRetrieval=true&serverTimezone=Asia/Shanghai\n          #username: root\n          #password: root\n          #driver-class-name: com.mysql.cj.jdbc.Driver\n  #redis 配置\n  redis:\n    database: 0\n    host: jeecg-boot-redis\n    lettuce:\n      pool:\n        max-active: 8   #最大连接数据库连接数,设 0 为没有限制\n        max-idle: 8     #最大等待连接中的数量,设 0 为没有限制\n        max-wait: -1ms  #最大建立连接等待时间。如果超过此时间将接到异常。设为-1表示无限制。\n        min-idle: 0     #最小等待连接中的数量,设 0 为没有限制\n      shutdown-timeout: 100ms\n    password:\n    port: 6379\n  #rabbitmq配置\n  rabbitmq:\n    host: 127.0.0.1\n    username: guest\n    password: guest\n    port: 5672\n    publisher-confirms: true\n    publisher-returns: true\n    virtual-host: /\n    listener:\n      simple:\n        acknowledge-mode: manual\n        #消费者的最小数量\n        concurrency: 1\n        #消费者的最大数量\n        max-concurrency: 1\n        #是否支持重试\n        retry:\n          enabled: true\n#jeecg专用配置\nminidao :\n  base-package: org.jeecg.modules.jmreport.*\n  db-type: mysql\njeecg :\n  # 本地：local\\Minio：minio\\阿里云：alioss\n  uploadType: local\n  path :\n    #文件上传根目录 设置\n    upload: D://opt//upFiles\n    #webapp文件路径\n    webapp: D://opt//webapp\n  shiro:\n    excludeUrls: /test/jeecgDemo/demo3,/test/jeecgDemo/redisDemo/**,/category/**,/visual/**,/map/**,/jmreport/bigscreen2/**\n  #阿里云oss存储配置\n  oss:\n    endpoint: oss-cn-beijing.aliyuncs.com\n    accessKey: ??\n    secretKey: ??\n    bucketName: jeecgdev\n    staticDomain: ??\n  # ElasticSearch 6设置\n  elasticsearch:\n    cluster-name: jeecg-ES\n    cluster-nodes: 127.0.0.1:9200\n    check-enabled: false\n  # 表单设计器配置\n  desform:\n    # 主题颜色（仅支持 16进制颜色代码）\n    theme-color: \"#1890ff\"\n    # 文件、图片上传方式，可选项：qiniu（七牛云）、system（跟随系统配置）\n    upload-type: system\n  # 在线预览文件服务器地址配置\n  file-view-domain: 127.0.0.1:8012\n  # minio文件上传\n  minio:\n    minio_url: http://minio.jeecg.com\n    minio_name: ??\n    minio_pass: ??\n    bucketName: otatest\n  #大屏报表参数设置\n  jmreport:\n    mode: dev\n    #是否需要校验token\n    is_verify_token: false\n    #必须校验方法\n    verify_methods: remove,delete,save,add,update\n  #Wps在线文档\n  wps:\n    domain: https://wwo.wps.cn/office/\n    appid: ??\n    appsecret: ??\n  #xxl-job配置\n  xxljob:\n    enabled: false\n    adminAddresses: http://jeecg-boot-xxljob:9080/xxl-job-admin\n    appname: ${spring.application.name}\n    accessToken: \'\'\n    logPath: logs/jeecg/job/jobhandler/\n    logRetentionDays: 30\n   #自定义路由配置 yml nacos database\n  route:\n    config:\n      data-id: jeecg-gateway-router\n      group: DEFAULT_GROUP\n      data-type: yml\n  #分布式锁配置\n  redisson:\n    address: jeecg-boot-redis:6379\n    password:\n    type: STANDALONE\n    enabled: true\n#Mybatis输出sql日志\nlogging:\n  level:\n    org.jeecg.modules.system.mapper : info\n#cas单点登录\ncas:\n  prefixUrl: http://localhost:8888/cas\n#swagger\nknife4j:\n  production: false\n  basic:\n    enable: false\n    username: jeecg\n    password: jeecg1314\n\n#第三方登录\njustauth:\n  enabled: true\n  type:\n    GITHUB:\n      client-id: ??\n      client-secret: ??\n      redirect-uri: http://sso.test.com:8080/jeecg-boot/thirdLogin/github/callback\n    WECHAT_ENTERPRISE:\n      client-id: ??\n      client-secret: ??\n      redirect-uri: http://sso.test.com:8080/jeecg-boot/thirdLogin/wechat_enterprise/callback\n      agent-id: 1000002\n    DINGTALK:\n      client-id: ??\n      client-secret: ??\n      redirect-uri: http://sso.test.com:8080/jeecg-boot/thirdLogin/dingtalk/callback\n  cache:\n    type: default\n    prefix: \'demo::\'\n    timeout: 1h', '9d25f4487829ccfc9416f01c483349e6', '2010-05-05 00:00:00', '2023-04-05 00:19:50', NULL, '10.18.0.2', 'D', '');
INSERT INTO `his_config_info` VALUES (2, 12, 'jeecg.yaml', 'DEFAULT_GROUP', '', 'server:\r\n  tomcat:\r\n    max-swallow-size: -1\r\n  error:\r\n    include-exception: true\r\n    include-stacktrace: ALWAYS\r\n    include-message: ALWAYS\r\n  compression:\r\n    enabled: true\r\n    min-response-size: 1024\r\n    mime-types: application/javascript,application/json,application/xml,text/html,text/xml,text/plain,text/css,image/*\r\nmanagement:\r\n  health:\r\n    mail:\r\n      enabled: false\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \"*\" #暴露所有节点\r\n    health:\r\n      sensitive: true #关闭过滤敏感信息\r\n  endpoint:\r\n    health:\r\n      show-details: ALWAYS  #显示详细信息\r\nspring:\r\n  servlet:\r\n    multipart:\r\n      max-file-size: 10MB\r\n      max-request-size: 10MB\r\n  mail:\r\n    host: smtp.163.com\r\n    username: jeecgos@163.com\r\n    password: ??\r\n    properties:\r\n      mail:\r\n        smtp:\r\n          auth: true\r\n          starttls:\r\n            enable: true\r\n            required: true\r\n  ## quartz定时任务,采用数据库方式\r\n  quartz:\r\n    job-store-type: jdbc\r\n    initialize-schema: embedded\r\n    #设置自动启动，默认为 true\r\n    auto-startup: true\r\n    #启动时更新己存在的Job\r\n    overwrite-existing-jobs: true\r\n    properties:\r\n      org:\r\n        quartz:\r\n          scheduler:\r\n            instanceName: MyScheduler\r\n            instanceId: AUTO\r\n          jobStore:\r\n            class: org.quartz.impl.jdbcjobstore.JobStoreTX\r\n            driverDelegateClass: org.quartz.impl.jdbcjobstore.StdJDBCDelegate\r\n            tablePrefix: QRTZ_\r\n            isClustered: true\r\n            misfireThreshold: 60000\r\n            clusterCheckinInterval: 10000\r\n          threadPool:\r\n            class: org.quartz.simpl.SimpleThreadPool\r\n            threadCount: 10\r\n            threadPriority: 5\r\n            threadsInheritContextClassLoaderOfInitializingThread: true\r\n  #json 时间戳统一转换\r\n  jackson:\r\n    date-format:   yyyy-MM-dd HH:mm:ss\r\n    time-zone:   GMT+8\r\n  aop:\r\n    proxy-target-class: true\r\n  activiti:\r\n    check-process-definitions: false\r\n    #启用作业执行器\r\n    async-executor-activate: false\r\n    #启用异步执行器\r\n    job-executor-activate: false\r\n  jpa:\r\n    open-in-view: false\r\n  #配置freemarker\r\n  freemarker:\r\n    # 设置模板后缀名\r\n    suffix: .ftl\r\n    # 设置文档类型\r\n    content-type: text/html\r\n    # 设置页面编码格式\r\n    charset: UTF-8\r\n    # 设置页面缓存\r\n    cache: false\r\n    prefer-file-system-access: false\r\n    # 设置ftl文件路径\r\n    template-loader-path:\r\n      - classpath:/templates\r\n  # 设置静态文件路径，js,css等\r\n  mvc:\r\n    static-path-pattern: /**\r\n  resource:\r\n    static-locations: classpath:/static/,classpath:/public/\r\n  autoconfigure:\r\n    exclude: com.alibaba.druid.spring.boot.autoconfigure.DruidDataSourceAutoConfigure\r\n#mybatis plus 设置\r\nmybatis-plus:\r\n  mapper-locations: classpath*:org/jeecg/modules/**/xml/*Mapper.xml\r\n  global-config:\r\n    # 关闭MP3.0自带的banner\r\n    banner: false\r\n    db-config:\r\n      #主键类型  0:\"数据库ID自增\",1:\"该类型为未设置主键类型\", 2:\"用户输入ID\",3:\"全局唯一ID (数字类型唯一ID)\", 4:\"全局唯一ID UUID\",5:\"字符串全局唯一ID (idWorker 的字符串表示)\";\r\n      id-type: ASSIGN_ID\r\n      # 默认数据库表下划线命名\r\n      table-underline: true\r\n  configuration:\r\n    # 这个配置会将执行的sql打印出来，在开发或测试的时候可以用\r\n    #log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\r\n    # 返回类型为Map,显示null对应的字段\r\n    call-setters-on-nulls: true', 'd695ddf9b45ff9f8e009803c93650263', '2010-05-05 00:00:00', '2023-04-05 00:19:53', NULL, '10.18.0.2', 'D', '');
INSERT INTO `his_config_info` VALUES (3, 13, 'jeecg-gateway-router.json', 'DEFAULT_GROUP', '', '[{\r\n  \"id\": \"jeecg-system\",\r\n  \"order\": 0,\r\n  \"predicates\": [{\r\n    \"name\": \"Path\",\r\n    \"args\": {\r\n      \"_genkey_0\": \"/sys/**\",\r\n      \"_genkey_1\": \"/eoa/**\",\r\n      \"_genkey_2\": \"/joa/**\",\r\n      \"_genkey_3\": \"/jmreport/**\",\r\n      \"_genkey_4\": \"/bigscreen/**\",\r\n      \"_genkey_5\": \"/desform/**\",\r\n      \"_genkey_6\": \"/online/**\",\r\n      \"_genkey_8\": \"/act/**\",\r\n      \"_genkey_9\": \"/plug-in/**\",\r\n      \"_genkey_10\": \"/generic/**\",\r\n      \"_genkey_11\": \"/v1/**\"\r\n    }\r\n  }],\r\n  \"filters\": [],\r\n  \"uri\": \"lb://jeecg-system\"\r\n}, {\r\n  \"id\": \"jeecg-demo\",\r\n  \"order\": 1,\r\n  \"predicates\": [{\r\n    \"name\": \"Path\",\r\n    \"args\": {\r\n      \"_genkey_0\": \"/mock/**\",\r\n      \"_genkey_1\": \"/test/**\",\r\n      \"_genkey_2\": \"/bigscreen/template1/**\",\r\n      \"_genkey_3\": \"/bigscreen/template2/**\"\r\n    }\r\n  }],\r\n  \"filters\": [],\r\n  \"uri\": \"lb://jeecg-demo\"\r\n}, {\r\n  \"id\": \"jeecg-system-websocket\",\r\n  \"order\": 2,\r\n  \"predicates\": [{\r\n    \"name\": \"Path\",\r\n    \"args\": {\r\n      \"_genkey_0\": \"/websocket/**\",\r\n      \"_genkey_1\": \"/eoaSocket/**\",\r\n      \"_genkey_2\": \"/newsWebsocket/**\"\r\n    }\r\n  }],\r\n  \"filters\": [],\r\n  \"uri\": \"lb:ws://jeecg-system\"\r\n}, {\r\n  \"id\": \"jeecg-demo-websocket\",\r\n  \"order\": 3,\r\n  \"predicates\": [{\r\n    \"name\": \"Path\",\r\n    \"args\": {\r\n      \"_genkey_0\": \"/vxeSocket/**\"\r\n    }\r\n  }],\r\n  \"filters\": [],\r\n  \"uri\": \"lb:ws://jeecg-demo\"\r\n}]', '82f4033ef6a51ce2ab6ce505be1b729a', '2010-05-05 00:00:00', '2023-04-05 00:19:57', NULL, '10.18.0.2', 'D', '');
INSERT INTO `his_config_info` VALUES (0, 14, 'gateway', 'DEFAULT_GROUP', '', 'spring:\r\n  cloud:\r\n    gateway:\r\n      httpclient:\r\n        responseTimeout: 60000\r\n      discovery:\r\n        locator:\r\n          enabled: true\r\n      globalcors:\r\n        cors-configurations:\r\n          \'[/**]\':\r\n            allowCredentials: true\r\n            allowedOrigins: \"*\"\r\n            allowedMethods: \"*\"\r\n            allowedHeaders: \"*\"\r\n      # 全局熔断降级配置\r\n      default-filters:\r\n        - name: SpecialHystrix\r\n          args:\r\n            name: default\r\n            #转发地址\r\n            fallbackUri: \'forward:/fallback\'\r\n            hystrix: \r\n              - /water-api/api/getData:longTime\r\n        - name: Retry\r\n          args:\r\n            #重试次数，默认值是 3 次\r\n            retries: 3\r\n            #HTTP 的状态返回码\r\n            statuses: BAD_GATEWAY,BAD_REQUEST\r\n            #指定哪些方法的请求需要进行重试逻辑，默认值是 GET 方法\r\n            #            methods: GET,POST\r\n            methods: GET\r\nribbon:\r\n  restclient:\r\n    enabled: true\r\n  ConnectTimeout: 6000\r\n  ReadTimeout: 600000\r\n  OkToRetryOnAllOperations: false\r\n  MaxAutoRetriesNextServer: 1\r\n  MaxAutoRetries: 1\r\n  eager-load:\r\n    enabled: true\r\n\r\nhystrix:\r\n  enabled: true\r\n  shareSecurityContext: true\r\n  command:\r\n    default:\r\n      execution:\r\n        isolation:\r\n          strategy: SEMAPHORE\r\n          thread:\r\n            timeoutInMilliseconds: 600000\r\n          semaphore:\r\n             #信号量最大请求数配置，允许并发量1000的请求，默认情况下是10，也就是超过10个的并发会直接进入fallback方                          #法，不会去真正请求\r\n            maxConcurrentRequests: 2000\r\n      fallback:\r\n        isolation:\r\n          strategy: SEMAPHORE\r\n          semaphore:\r\n            maxConcurrentRequests: 1000\r\n    longTime:\r\n      execution:\r\n        isolation:\r\n          strategy: SEMAPHORE\r\n          thread:\r\n            timeoutInMilliseconds: 60000\r\n          semaphore:\r\n             #信号量最大请求数配置，允许并发量1000的请求，默认情况下是10，也就是超过10个的并发会直接进入fallback方                          #法，不会去真正请求\r\n            maxConcurrentRequests: 2000\r\n      fallback:\r\n        isolation:\r\n          strategy: SEMAPHORE\r\n          semaphore:\r\n            maxConcurrentRequests: 1000  \r\n\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \"*\" #暴露所有节点\r\n    health:\r\n      sensitive: true #关闭过滤敏感信息\r\n\r\n\r\n\r\nexcludeUrls: /sys/api/*,/water-api/pgdata/**\r\n#url传参方式校验的token\r\nrequestParamVerifyTokenUrl: /water-manager/download\r\nlogLevel: info\r\nlogging.config: classpath:logback-level.xml', '858a27fe7f699ee68ffff17ad2c3b31d', '2010-05-05 00:00:00', '2023-04-05 00:21:34', NULL, '10.18.0.2', 'I', '');
INSERT INTO `his_config_info` VALUES (0, 15, 'gateway-router.json', 'DEFAULT_GROUP', '', '[{\r\n  \"id\": \"system\",\r\n  \"order\": 0,\r\n  \"predicates\": [{\r\n    \"name\": \"Path\",\r\n    \"args\": {\r\n      \"_genkey_0\": \"/sys/**\",\r\n      \"_genkey_1\": \"/eoa/**\",\r\n      \"_genkey_2\": \"/joa/**\",\r\n      \"_genkey_3\": \"/jmreport/**\",\r\n      \"_genkey_4\": \"/bigscreen/**\",\r\n      \"_genkey_5\": \"/desform/**\",\r\n      \"_genkey_6\": \"/online/**\",\r\n      \"_genkey_8\": \"/act/**\",\r\n      \"_genkey_9\": \"/plug-in/**\",\r\n      \"_genkey_10\": \"/generic/**\",\r\n      \"_genkey_11\": \"/v1/**\"\r\n    }\r\n  }],\r\n  \"filters\": [],\r\n  \"uri\": \"lb://system\"\r\n},  {\r\n  \"id\": \"jeecg-system-websocket\",\r\n  \"order\": 1,\r\n  \"predicates\": [{\r\n    \"name\": \"Path\",\r\n    \"args\": {\r\n      \"_genkey_0\": \"/websocket/**\",\r\n      \"_genkey_1\": \"/eoaSocket/**\",\r\n      \"_genkey_2\": \"/newsWebsocket/**\",\r\n      \"_genkey_3\": \"/backstage/**\"\r\n    }\r\n  }],\r\n  \"filters\": [],\r\n  \"uri\": \"lb:ws://water-system\"\r\n}, {\r\n  \"id\": \"manager\",\r\n  \"order\": -1,\r\n  \"predicates\": [{\r\n    \"name\": \"Path\",\r\n    \"args\": {\r\n      \"_genkey_0\": \"/water-manager/waterDataRecord/validExportXls\",\r\n    }\r\n  }],\r\n  \"filters\": [{\"args\":{\"parts\":1},\"name\":\"StripPrefix\"}],\r\n  \"metadata\":{\r\n      \"response-timeout\":100000,\r\n      \"connect-timeout\":100000\r\n  },\r\n  \"uri\": \"lb://manager\"\r\n}]', '7cd3aba887f67ea4fd278d4ed81e59d5', '2010-05-05 00:00:00', '2023-04-05 00:23:48', NULL, '10.18.0.2', 'I', '');
