rentRequests: 2000\n      fallback:\n        isolation:\n          strategy: SEMAPHORE\n          semaphore:\n            maxConcurrentRequests: 1000  \n\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"*\" #暴露所有节点\n    health:\n      sensitive: true #关闭过滤敏感信息\n\n\n\nexcludeUrls: /sys/api/*\n#url传参方式校验的token\nrequestParamVerifyTokenUrl: /water-manager/download\nlogLevel: info\nlogging.config: classpath:logback-level.xml', '0176bfb09515cea8d607446a8d3da74e', '2010-05-05 00:00:00', '2023-04-05 00:58:14', NULL, '10.18.0.2', 'D', '');
